package com.jaxio.printer;

import java.util.Locale;

import javax.inject.Named;
import javax.inject.Singleton;

import com.jaxio.domain.Currency;
import com.jaxio.domain.Currency_;
import com.jaxio.printer.support.GenericPrinter;

/**
 * {@link GenericPrinter} for {@link Currency} 
 *
 * @see TypeAwarePrinter
 */
@Named
@Singleton
public class CurrencyPrinter extends GenericPrinter<Currency> {
    public CurrencyPrinter() {
        super(Currency.class, Currency_.name.getName());
    }

    @Override
    public String print(Currency currency, Locale locale) {
        if (currency == null) {
            return "";
        }
        StringBuilder ret = new StringBuilder();
        appendIfNotEmpty(ret, currency.getName());
        return ret.toString();
    }
}