package com.jaxio.printer;

import java.util.Locale;

import javax.inject.Named;
import javax.inject.Singleton;

import com.jaxio.domain.Customer;
import com.jaxio.domain.Customer_;
import com.jaxio.printer.support.GenericPrinter;

/**
 * {@link GenericPrinter} for {@link Customer} 
 *
 * @see TypeAwarePrinter
 */
@Named
@Singleton
public class CustomerPrinter extends GenericPrinter<Customer> {
    public CustomerPrinter() {
        super(Customer.class, Customer_.companyName.getName());
    }

    @Override
    public String print(Customer customer, Locale locale) {
        if (customer == null) {
            return "";
        }
        StringBuilder ret = new StringBuilder();
        appendIfNotEmpty(ret, customer.getCompanyName());
        return ret.toString();
    }
}