package com.jaxio.printer;

import java.util.Locale;

import javax.inject.Named;
import javax.inject.Singleton;

import com.jaxio.domain.Transaction;
import com.jaxio.domain.Transaction_;
import com.jaxio.printer.support.GenericPrinter;

/**
 * {@link GenericPrinter} for {@link Transaction} 
 *
 * @see TypeAwarePrinter
 */
@Named
@Singleton
public class TransactionPrinter extends GenericPrinter<Transaction> {
    public TransactionPrinter() {
        super(Transaction.class, Transaction_.description.getName());
    }

    @Override
    public String print(Transaction transaction, Locale locale) {
        if (transaction == null) {
            return "";
        }
        StringBuilder ret = new StringBuilder();
        appendIfNotEmpty(ret, transaction.getDescription());
        return ret.toString();
    }
}