import com.jaxio.repository.CustomerRepository;

/**
 * Integration test on CustomerRepository
 */
@RunWith(SpringJUnit4ClassRunner.class)
@ContextConfiguration(locations = { "classpath*:spring/applicationContext-test.xml" })
@Transactional
public class CustomerRepositoryIT {
    @SuppressWarnings("unused")
    private static final Logger log = LoggerFactory.getLogger(CustomerRepositoryIT.class);

    @PersistenceContext
    private EntityManager entityManager;

    @Inject
    private CustomerRepository customerRepository;

    @Inject
    private CustomerGenerator customerGenerator;

    @Test
    @Rollback
    public void saveAndGet() {
        Customer customer = customerGenerator.getCustomer();

        // add it to a Set before saving (force equals/hashcode)
        Set<Customer> set = newHashSet(customer, customer);
        assertThat(set).hasSize(1);

        customerRepository.save(customer);
        entityManager.flush();

        // reload it from cache and check equality
        Customer model = new Customer();
        model.setId(customer.getId());
        assertThat(customer).isEqualTo(customerRepository.get(model));

        // clear cache to force reload from db
        entityManager.clear();

        // pk are equals...
        assertThat(customer.getId()).isEqualTo(customerRepository.get(model).getId());

        // but since you do not use a business key, equality is lost.
        assertThat(customer).isNotEqualTo(customerRepository.get(model));
    }
}