package com.jaxio.repository;

import java.math.BigDecimal;

import javax.inject.Inject;
import javax.inject.Named;
import javax.inject.Singleton;

import com.jaxio.domain.Account;
import com.jaxio.domain.Currency;
import com.jaxio.domain.Transaction;
import com.jaxio.repository.AccountGenerator;
import com.jaxio.repository.AccountRepository;
import com.jaxio.repository.CurrencyGenerator;
import com.jaxio.repository.CurrencyRepository;

/**
 * Helper class to create transient entities instance for testing purposes.
 * Simple properties are pre-filled with random values.
 */
@Named
@Singleton
public class TransactionGenerator {

    /**
     * Returns a new Transaction instance filled with random values.
     */
    public Transaction getTransaction() {
        Transaction transaction = new Transaction();

        // simple attributes follows
        transaction.setAmount(BigDecimal.ONE);
        transaction.setTransactionDate(new org.joda.time.LocalDate());
        transaction.setValueDate(new org.joda.time.LocalDate());
        transaction.setDescription("a");
        // mandatory relation
        Currency currency = currencyGenerator.getCurrency();
        currencyRepository.save(currency);
        transaction.setCurrency(currency);
        // mandatory relation
        Account account = accountGenerator.getAccount();
        accountRepository.save(account);
        transaction.setAccount(account);
        return transaction;
    }

    @Inject
    private CurrencyRepository currencyRepository;
    @Inject
    private CurrencyGenerator currencyGenerator;
    @Inject
    private AccountRepository accountRepository;
    @Inject
    private AccountGenerator accountGenerator;
}