package com.jaxio.web.domain;

import javax.inject.Inject;
import javax.inject.Named;
import javax.inject.Singleton;

import com.jaxio.domain.Customer;
import com.jaxio.repository.CustomerRepository;
import com.jaxio.repository.support.EntityGraphLoader;

/**
 * Preloads the {@link Customer} associations required by the view layer.
 */
@Named
@Singleton
public class CustomerGraphLoader extends EntityGraphLoader<Customer, Integer> {
    // required by cglib to create a proxy around the object as we are using the @Transactional annotation
    protected CustomerGraphLoader() {
        super();
    }

    @Inject
    public CustomerGraphLoader(CustomerRepository customerRepository) {
        super(customerRepository);
    }

    @Override
    public void loadGraph(Customer customer) {
        loadSingular(customer.getAddress());
        loadCollection(customer.getAccounts());
    }
}