package com.jaxio.repository;

import static org.fest.assertions.Assertions.assertThat;
import static org.mockito.Matchers.any;
import static org.mockito.Mockito.times;
import static org.mockito.Mockito.verify;
import static org.mockito.Mockito.when;

import javax.persistence.NoResultException;
import javax.persistence.NonUniqueResultException;

import org.junit.Test;
import org.junit.runner.RunWith;
import org.mockito.InjectMocks;
import org.mockito.Mock;
import org.mockito.runners.MockitoJUnitRunner;

import com.jaxio.dao.TransactionDao;
import com.jaxio.dao.support.SearchParameters;
import com.jaxio.domain.Transaction;
import com.jaxio.repository.TransactionRepository;

/**
 * Unit test on TransactionRepository
 */
@RunWith(MockitoJUnitRunner.class)
public class TransactionRepositoryTest {

    @Mock
    private TransactionDao transactionDao;

    @InjectMocks
    private TransactionRepository transactionRepository;

    @Test
    public void findUniqueOrNoneReturnsNull() {
        when(transactionDao.findUniqueOrNone(any(Transaction.class), any(SearchParameters.class))).thenReturn(null);

        Transaction result = transactionRepository.findUniqueOrNone(new Transaction());

        assertThat(result).isNull();
        verify(transactionDao, times(1)).findUniqueOrNone(any(Transaction.class), any(SearchParameters.class));
    }

    @Test
    public void findUniqueOrNoneReturnsSingleValue() {
        Transaction unique = new Transaction();

        when(transactionDao.findUniqueOrNone(any(Transaction.class), any(SearchParameters.class))).thenReturn(unique);

        Transaction result = transactionRepository.findUniqueOrNone(new Transaction());

        assertThat(result).isNotNull().isSameAs(unique);
        verify(transactionDao, times(1)).findUniqueOrNone(any(Transaction.class), any(SearchParameters.class));
    }

    @SuppressWarnings("unchecked")
    @Test(expected = NonUniqueResultException.class)
    public void findUniqueOrNoneThrowsExceptionWhenNonUniqueResults() {
        when(transactionDao.findUniqueOrNone(any(Transaction.class), any(SearchParameters.class))).thenThrow(NonUniqueResultException.class);

        transactionRepository.findUniqueOrNone(new Transaction());
    }

    @SuppressWarnings("unchecked")
    @Test(expected = NoResultException.class)
    public void findUniqueThrowsExceptionWhenNoResult() {
        when(transactionDao.findUnique(any(Transaction.class), any(SearchParameters.class))).thenThrow(NoResultException.class);

        transactionRepository.findUnique(new Transaction());
    }

    @Test
    public void findUnique() {
        Transaction unique = new Transaction();

        when(transactionDao.findUnique(any(Transaction.class), any(SearchParameters.class))).thenReturn(unique);

        Transaction result = transactionRepository.findUnique(new Transaction());

        assertThat(result).isNotNull();
        verify(transactionDao, times(1)).findUnique(any(Transaction.class), any(SearchParameters.class));
    }

    @SuppressWarnings("unchecked")
    @Test(expected = NonUniqueResultException.class)
    public void findUniqueThrowsExeptionWhenNonUniqueResult() {
        when(transactionDao.findUnique(any(Transaction.class), any(SearchParameters.class))).thenThrow(NonUniqueResultException.class);

        transactionRepository.findUnique(new Transaction());
    }
}