package com.jaxio.web.selenium.page.account;

import static org.openqa.selenium.By.name;
import org.openqa.selenium.WebElement;
import org.openqa.selenium.support.FindBy;
import com.jaxio.web.selenium.page.AbstractEditPage;
import com.jaxio.web.selenium.support.Page;

@Page
public class AccountEditPage extends AbstractEditPage {
    // edit box
    @FindBy(id = "form:username")
    public WebElement username;
    @FindBy(id = "form:password")
    public WebElement password;
    @FindBy(id = "form:email")
    public WebElement email;
    @FindBy(id = "form:isEnabled")
    public WebElement isEnabled;
    @FindBy(id = "form:civility")
    public WebElement civility;
    @FindBy(id = "form:firstName")
    public WebElement firstName;
    @FindBy(id = "form:lastName")
    public WebElement lastName;
    @FindBy(id = "form:birthDate")
    public WebElement birthDate;
    @FindBy(id = "form:description")
    public WebElement description;

    // many-to-one homeAddress
    @FindBy(id = "form:homeAddress")
    public WebElement homeAddress;
    @FindBy(id = "form:selectHomeAddress")
    public WebElement homeAddressSelectButton;
    @FindBy(id = "form:editHomeAddress")
    public WebElement homeAddressEditButton;
    @FindBy(id = "form:viewHomeAddress")
    public WebElement homeAddressViewButton;

    // tabs
    @FindBy(linkText = "Cool Books")
    public WebElement coolBooksTab;

    @FindBy(linkText = "Edocs")
    public WebElement edocsTab;

    @FindBy(linkText = "Security Roles")
    public WebElement securityRolesTab;

    // Cool Books tab
    public void openBookTab() {
        client.click(coolBooksTab);
    }

    public void searchBook() {
        clickSearch("Cool Books");
    }

    public void addBook() {
        clickAdd("Cool Books");
    }

    public void editBook(String coolBooks) {
        clickEdit(coolBooks);
    }

    public void deleteBook(String coolBooks) {
        clickDelete(coolBooks);
        client.click(name("form:askForRemoveBookDialogYes"));
    }

    public void selectCoolBooks(String coolBooks) {
        clickSelect(coolBooks);
    }

    public void openEdocTab() {
        client.click(edocsTab);
    }

    public void searchEdoc() {
        clickSearch("Edocs");
    }

    public void addEdoc() {
        clickAdd("Edocs");
    }

    public void editEdoc(String edocs) {
        clickEdit(edocs);
    }

    public void deleteEdoc(String edocs) {
        clickDelete(edocs);
        client.click(name("form:askForRemoveEdocDialogYes"));
    }

    public void selectEdocs(String edocs) {
        clickSelect(edocs);
    }

    public void openSecurityRoleTab() {
        client.click(securityRolesTab);
    }

    public void searchSecurityRole() {
        clickSearch("Security Roles");
    }

    public void addSecurityRole() {
        clickAdd("Security Roles");
    }

    public void editSecurityRole(String securityRoles) {
        clickEdit(securityRoles);
    }

    public void deleteSecurityRole(String securityRoles) {
        clickDelete(securityRoles);
        client.click(name("form:askForRemoveSecurityRoleDialogYes"));
    }

    public void selectSecurityRoles(String securityRoles) {
        clickSelect(securityRoles);
    }

    public void update(String username, String password, String email) {
        client.fill(this.username, username);
        client.fill(this.password, password);
        client.fill(this.email, email);
    }

}