package com.jaxio.web.selenium.page.legacy;

import static org.openqa.selenium.By.name;

import org.openqa.selenium.WebElement;
import org.openqa.selenium.support.FindBy;

import com.jaxio.web.selenium.support.Page;
import com.jaxio.web.selenium.page.AbstractSearchPage;

@Page
public class LegacySearchPage extends AbstractSearchPage {
    // search box
    @FindBy(id = "form:name_input")
    public WebElement name;
    @FindBy(id = "form:code_input")
    public WebElement code;
    @FindBy(id = "form:extraInfo_input")
    public WebElement extraInfo;

    public void editLegacy(String legacy) {
        clickEdit(legacy);
    }

    public void addLegacy(String legacy) {
        clickAdd(legacy);
    }

    public void deleteLegacy(String legacy) {
        clickDelete(legacy);
        client.click(name("form:askForRemoveLegacyDialogYes"));
    }

    public void selectLegacy(String legacy) {
        clickSelect(legacy);
    }
}