package com.jaxio.web.selenium.page.role;

import static org.openqa.selenium.By.name;

import org.openqa.selenium.WebElement;
import org.openqa.selenium.support.FindBy;

import com.jaxio.web.selenium.support.Page;
import com.jaxio.web.selenium.page.AbstractSearchPage;

@Page
public class RoleSearchPage extends AbstractSearchPage {
    // search box
    @FindBy(id = "form:roleName_input")
    public WebElement roleName;

    public void editRole(String role) {
        clickEdit(role);
    }

    public void addRole(String role) {
        clickAdd(role);
    }

    public void deleteRole(String role) {
        clickDelete(role);
        client.click(name("form:askForRemoveRoleDialogYes"));
    }

    public void selectRole(String role) {
        clickSelect(role);
    }

    public void searchByRolename(String roleName) {
        client.autocomplete(this.roleName, roleName);
        client.click(searchButton);
    }
}