package com.jaxio.appli.web.domain;

import static org.apache.commons.lang.StringUtils.join;

import javax.inject.Inject;
import javax.inject.Named;

import com.jaxio.appli.domain.Account;
import com.jaxio.appli.web.domain.support.GenericExcelExporter;
import com.jaxio.appli.web.faces.ConversationContextScoped;

/**
 * Exports to excel document {@link Account} search criteria and result. 
 */
@Named
@ConversationContextScoped
public class AccountExcelExporter extends GenericExcelExporter<Account> {
    @Inject
    protected AccountSearchForm sf;

    public AccountExcelExporter() {
        super("account_username", "account_email", "account_isEnabled", "account_homeAddress");
    }

    @Override
    protected void fillResultItem(int row, Account item) {
        int col = 0;
        setValue(row, col++, item.getUsername());
        setValue(row, col++, item.getEmail());
        setValue(row, col++, item.getIsEnabled());
        setEntity(row, col++, item.getHomeAddress());
    }

    @Override
    public void fillSearchCriteria(int row) {
        useCriteriaSheet();
        setLeftHeader(row, 0, "search_full_text");
        setValue(row++, 1, join(sf.getTermsOnAll().getSelected(), ' '));
        setTermSelector(row++, 0, "account_username", sf.getUsernameTermSelector());
        setTermSelector(row++, 0, "account_email", sf.getEmailTermSelector());
        setTermSelector(row++, 0, "account_firstName", sf.getFirstNameTermSelector());
        setTermSelector(row++, 0, "account_lastName", sf.getLastNameTermSelector());

        setSelector(row++, 0, "account_username", sf.getUsernameSelector());
        setSelector(row++, 0, "account_password", sf.getPasswordSelector());
        setSelector(row++, 0, "account_email", sf.getEmailSelector());
        setSelector(row++, 0, "account_isEnabled", sf.getIsEnabledSelector());
        setSelector(row++, 0, "account_civility", sf.getCivilitySelector());
        setSelector(row++, 0, "account_firstName", sf.getFirstNameSelector());
        setSelector(row++, 0, "account_lastName", sf.getLastNameSelector());
        setRangeDateTime(row++, 0, "account_birthDate", sf.getBirthDateRange());
        setSelector(row++, 0, "account_description", sf.getDescriptionSelector());
        setSelectedEntities(row++, 0, "account_homeAddress", sf.getHomeAddressSelector().getSelected());
        setSelectedEntities(row++, 0, "account_securityRoles", sf.getAccount().getSecurityRoles());
    }
}