package com.jaxio.appli.web.domain;

import static org.apache.commons.lang.StringUtils.join;

import javax.inject.Inject;
import javax.inject.Named;

import com.jaxio.appli.domain.Book;
import com.jaxio.appli.web.domain.support.GenericExcelExporter;
import com.jaxio.appli.web.faces.ConversationContextScoped;

/**
 * Exports to excel document {@link Book} search criteria and result. 
 */
@Named
@ConversationContextScoped
public class BookExcelExporter extends GenericExcelExporter<Book> {
    @Inject
    protected BookSearchForm sf;

    public BookExcelExporter() {
        super("book_owner", "book_bookTitle", "book_numberOfPages");
    }

    @Override
    protected void fillResultItem(int row, Book item) {
        int col = 0;
        setEntity(row, col++, item.getOwner());
        setValue(row, col++, item.getBookTitle());
        setValue(row, col++, item.getNumberOfPages());
    }

    @Override
    public void fillSearchCriteria(int row) {
        useCriteriaSheet();
        setLeftHeader(row, 0, "search_full_text");
        setValue(row++, 1, join(sf.getTermsOnAll().getSelected(), ' '));
        setTermSelector(row++, 0, "book_bookTitle", sf.getBookTitleTermSelector());
        setTermSelector(row++, 0, "book_numberOfPages", sf.getNumberOfPagesTermSelector());

        setSelectedEntities(row++, 0, "book_owner", sf.getOwnerSelector().getSelected());
        setSelector(row++, 0, "book_bookTitle", sf.getBookTitleSelector());
        setRangeNumber(row++, 0, "book_numberOfPages", sf.getNumberOfPagesRange());
    }
}