package com.jaxio.appli.web.domain;

import static org.apache.commons.lang.StringUtils.join;

import javax.inject.Inject;
import javax.inject.Named;

import com.jaxio.appli.domain.Document;
import com.jaxio.appli.web.domain.support.GenericExcelExporter;
import com.jaxio.appli.web.faces.ConversationContextScoped;

/**
 * Exports to excel document {@link Document} search criteria and result. 
 */
@Named
@ConversationContextScoped
public class DocumentExcelExporter extends GenericExcelExporter<Document> {
    @Inject
    protected DocumentSearchForm sf;

    public DocumentExcelExporter() {
        super("document_owner", "document_documentFileName");
    }

    @Override
    protected void fillResultItem(int row, Document item) {
        int col = 0;
        setEntity(row, col++, item.getOwner());
        setValue(row, col++, item.getDocumentFileName());
    }

    @Override
    public void fillSearchCriteria(int row) {
        useCriteriaSheet();
        setLeftHeader(row, 0, "search_full_text");
        setValue(row++, 1, join(sf.getTermsOnAll().getSelected(), ' '));
        setTermSelector(row++, 0, "document_documentBinary", sf.getDocumentBinaryTermSelector());
        setTermSelector(row++, 0, "document_documentFileName", sf.getDocumentFileNameTermSelector());

        setSelectedEntities(row++, 0, "document_owner", sf.getOwnerSelector().getSelected());
    }
}