package com.jaxio.web.domain;

import static org.apache.commons.lang.StringUtils.join;

import javax.inject.Inject;
import javax.inject.Named;

import com.jaxio.domain.Account;
import com.jaxio.web.domain.support.GenericExcelExporter;
import com.jaxio.web.faces.ConversationContextScoped;

/**
 * View Helper to search {@link Account}.
 * It exposes a {@link Account} instance so it can be used in search by-example-query.
 */
@Named
@ConversationContextScoped
public class AccountExcelExporter extends GenericExcelExporter<Account> {
    @Inject
    protected AccountSearchForm sf;

    public AccountExcelExporter() {
        super("account_accountNumber", "account_name", "account_customer");
    }

    @Override
    protected void fillResultItem(int row, Account item) {
        int col = 0;
        setValue(row, col++, item.getAccountNumber());
        setValue(row, col++, item.getName());
        setEntity(row, col++, item.getCustomer());
    }

    @Override
    public void fillSearchCriteria(int row) {
        useCriteriaSheet();

        setSelector(row++, 0, "account_accountNumber", sf.getAccountNumberSelector());
        setSelector(row++, 0, "account_name", sf.getNameSelector());
        setSelectedEntities(row++, 0, "account_currency", sf.getCurrencySelector().getSelected());
        setSelectedEntities(row++, 0, "account_customer", sf.getCustomerSelector().getSelected());
    }
}