package com.jaxio.web.domain;

import static org.apache.commons.lang.StringUtils.join;

import javax.inject.Inject;
import javax.inject.Named;

import com.jaxio.domain.Currency;
import com.jaxio.web.domain.support.GenericExcelExporter;
import com.jaxio.web.faces.ConversationContextScoped;

/**
 * View Helper to search {@link Currency}.
 * It exposes a {@link Currency} instance so it can be used in search by-example-query.
 */
@Named
@ConversationContextScoped
public class CurrencyExcelExporter extends GenericExcelExporter<Currency> {
    @Inject
    protected CurrencySearchForm sf;

    public CurrencyExcelExporter() {
        super("currency_code", "currency_name", "currency_decimalCount");
    }

    @Override
    protected void fillResultItem(int row, Currency item) {
        int col = 0;
        setValue(row, col++, item.getCode());
        setValue(row, col++, item.getName());
        setValue(row, col++, item.getDecimalCount());
    }

    @Override
    public void fillSearchCriteria(int row) {
        useCriteriaSheet();

        setSelector(row++, 0, "currency_code", sf.getCodeSelector());
        setSelector(row++, 0, "currency_name", sf.getNameSelector());
        setRangeNumber(row++, 0, "currency_decimalCount", sf.getDecimalCountRange());
    }
}