package com.jaxio.web.domain;

import javax.inject.Inject;
import javax.inject.Named;

import com.jaxio.domain.Customer;
import com.jaxio.web.domain.support.GenericExcelExporter;
import com.jaxio.web.faces.ConversationContextScoped;

/**
 * Exports to excel document {@link Customer} search criteria and result. 
 */
@Named
@ConversationContextScoped
public class CustomerExcelExporter extends GenericExcelExporter<Customer> {
    @Inject
    protected CustomerSearchForm sf;

    public CustomerExcelExporter() {
        super("customer_companyName", //
                "customer_contractFileName");
    }

    @Override
    protected void fillResultItem(int row, Customer item) {
        int col = 0;
        setValue(row, col++, item.getCompanyName());
        setValue(row, col++, item.getContractFileName());
    }

    @Override
    public void fillSearchCriteria(int row) {
        useCriteriaSheet();

        setSelector(row++, 0, "customer_companyName", sf.getCompanyNameSelector());
        setSelectedEntities(row++, 0, "customer_address", sf.getAddressSelector().getSelected());
    }
}